MODULE GenMod_Constants

USE GenMod_Core, ONLY: &
    MaxFileNameLen

USE GenMod_MiscUMScienceConstants, ONLY : &
    Earth_Radius,                         &
    EPSILON,                              &
    RMDI,                                 &
    PI

IMPLICIT NONE

PRIVATE            :: RMDI
PRIVATE            :: PI
PRIVATE            :: EPSILON
PRIVATE            :: Earth_Radius
PRIVATE            :: MaxFileNameLen

! PUBLIC parameters
INTEGER, PARAMETER :: TLen = 1551   ! Number of elements in look-up table.
                                    ! Hard wired. Could be calculated by
                                    ! NINT((THiBound - TLoBound)/TInc) + 1
                                    ! except this won't compile!

REAL, PARAMETER    :: TwoPi = 2.0 * PI

REAL, PARAMETER    :: EarthRadiusInverse = 1.0 / Earth_Radius

REAL, PARAMETER    :: EarthSurface = 4.0 * PI * Earth_Radius * Earth_Radius

REAL, PARAMETER    :: OMEGA = 7.292116E-5 ! Earths angular velocity
REAL, PARAMETER    :: TWO_OMEGA = 2.0 * OMEGA

REAL, PARAMETER    :: GasRatio = EPSILON ! Ratio of dry to moist gas constants

REAL, PARAMETER    :: TInc = 0.1    ! Temperature increment in
                                    ! look-up table for svp and its
                                    ! derivative. K

REAL, PARAMETER    :: TLoBound = 183.15 ! Lower temperature bound is -90C
REAL, PARAMETER    :: THiBound = 338.15 ! Upper temperature bound is 65C

!Global scalar:
REAL,TARGET        :: svp(TLen)     ! UM svp of water array (over water / ice)
REAL,TARGET        :: dlsvp(TLen)   ! Logarithmic T derivative of UM svp array
REAL,TARGET        :: svp_W(TLen)   ! UM svp of water array (over water only)
REAL,TARGET        :: dlsvp_W(TLen) ! Logarithmic T derivative of UM svp_W array

!Local scalar:
INTEGER            :: Loop          ! Loop variable in look-up table.

